#include<stdio.h>
int main(){
    int a,b;
    printf("Enter the two number:");
    scanf("%d%d",&a,&b);
    printf("\nAddition Of Number is:%d",(a+b));
    printf("\nSubtraction Of Number is:%d",(a-b));
    printf("\nMultiplication Of Number is:%d",(a*b));
    printf("\nDivision Of Number is:%d",(a/b));
    return 0;

}